import {createSlice} from '@reduxjs/toolkit';

const initialCartState = {
    showCart: false
}

const cartSlice = createSlice({
    name: 'cart',
    initialState: initialCartState,
    reducers: {
        showCart(state){
            state.showCart = true
        }
    }
});

export const cartAction = cartSlice.actions;
export default cartSlice.reducer;
